#!/bin/bash

# Barcha servislarni to'liq tekshirish

set -e

cd /var/www/news.acoustic.uz

echo "🎉 Barcha servislarni to'liq tekshirish..."

# 1. PM2 statusini ko'rsatish
echo "📊 PM2 status:"
pm2 status

# 2. Backend ni test qilish
echo ""
echo "🧪 Backend ni test qilish..."
BACKEND_STATUS=$(curl -s -o /dev/null -w "%{http_code}" http://localhost:3001/api/docs || echo "000")
if [ "$BACKEND_STATUS" = "200" ] || [ "$BACKEND_STATUS" = "301" ] || [ "$BACKEND_STATUS" = "302" ]; then
    echo "✅ Backend ishlayapti! (HTTP $BACKEND_STATUS)"
    echo "   URL: http://localhost:3001/api/docs"
else
    echo "⚠️ Backend javob bermayapti (HTTP $BACKEND_STATUS)"
fi

# 3. Frontend ni test qilish
echo ""
echo "🧪 Frontend ni test qilish..."
FRONTEND_STATUS=$(curl -s -o /dev/null -w "%{http_code}" http://localhost:3000 || echo "000")
if [ "$FRONTEND_STATUS" = "200" ] || [ "$FRONTEND_STATUS" = "301" ] || [ "$FRONTEND_STATUS" = "302" ]; then
    echo "✅ Frontend ishlayapti! (HTTP $FRONTEND_STATUS)"
    echo "   URL: http://localhost:3000"
else
    echo "⚠️ Frontend javob bermayapti (HTTP $FRONTEND_STATUS)"
fi

# 4. Admin panel ni test qilish
echo ""
echo "🧪 Admin panel ni test qilish..."
ADMIN_STATUS=$(curl -s -o /dev/null -w "%{http_code}" http://admins.acoustic.uz || echo "000")
if [ "$ADMIN_STATUS" = "200" ] || [ "$ADMIN_STATUS" = "301" ] || [ "$ADMIN_STATUS" = "302" ]; then
    echo "✅ Admin panel ishlayapti! (HTTP $ADMIN_STATUS)"
    echo "   URL: http://admins.acoustic.uz"
else
    echo "⚠️ Admin panel javob bermayapti (HTTP $ADMIN_STATUS)"
fi

# 5. Admin panel dist papkasini tekshirish
echo ""
echo "📋 Admin panel dist papkasini tekshirish..."
if [ -d "/var/www/admins.acoustic.uz/dist" ]; then
    echo "✅ Admin panel dist papkasi mavjud!"
    ls -la /var/www/admins.acoustic.uz/dist | head -5
else
    echo "⚠️ Admin panel dist papkasi topilmadi!"
fi

# 6. Nginx ni test qilish
echo ""
echo "🧪 Nginx ni test qilish..."
if systemctl is-active --quiet nginx; then
    echo "✅ Nginx ishlayapti!"
else
    echo "⚠️ Nginx ishlamayapti!"
fi

# 7. Portlarni tekshirish
echo ""
echo "📋 Portlarni tekshirish..."
if lsof -i:3000 >/dev/null 2>&1; then
    echo "✅ Port 3000 (Frontend) ishlatilmoqda"
else
    echo "⚠️ Port 3000 (Frontend) bo'sh"
fi

if lsof -i:3001 >/dev/null 2>&1; then
    echo "✅ Port 3001 (Backend) ishlatilmoqda"
else
    echo "⚠️ Port 3001 (Backend) bo'sh"
fi

# 8. Xulosa
echo ""
echo "✅ Barcha servislar tekshirildi!"
echo ""
